import copyClipboard from '~/components/utils/copyClipboard'
import axios from 'axios'

export default {
  components:{
      HeroHeader,FooterComp
  },
  data(){
      return{
          templates:[]
      }
  },
  async mounted(){
      const res = await axios('https://cloud.preso.cc/commands.json')
      const dt = res.data
      let list = dt.list || []
      list = list.filter(d => !d.private)
      list.forEach(d => d.copy = false)
      this.templates = list
  },
  head () {
        let cover = 'rwi-splash.jpg'
        const title = 'React With Image for Twitter'
        return {
            title,
            meta: [
                { hid:'ogtit', property: 'og:title', content: title },
                { hid:'twtit', name: 'twitter:title', content: title },
                { hid:'ogimg', property: 'og:image', content: `https://www.presenta.cc/${cover}` },
                { hid:'twimg', name: 'twitter:image', content: `https://www.presenta.cc/${cover}` },
                { hid:'ogurl', property: 'og:url', content: `https://www.presenta.cc/rwi` },
                { hid:'twurl', name: 'twitter:url', content: `https://www.presenta.cc/rwi` }
            ]
        }
        
  },
  methods:{
      copy(t){
          t.copy=true
          copyClipboard('@ReactWithImage ' + t.command)
          setTimeout(() => t.copy = false, 150)
      }
  }
}

</script>



